import { useState } from "react";

export const useForn = ( initialForm = {} ) => {

    const [ formState, setFormState ] = useState( initialForm ) ;

    const onInputChange = ( { target } ) => {
        const { name, value } = target
        setFormState( {
            ...formState,
            [ name ]: value
        } )
    }

    const onResetForm = ( { target } ) => {
        const { name, value } = target
        setFormState( {
            ...formState,
            [ name ]: value
        } )
    }

    return {
        ...formState,
        formState,
        onInputChange,
        onResetForm
    }
}
