import { useLayoutEffect, useRef, useState } from "react";

export const Quote = ({ quote, author }) => {

    useLayoutEffect(() => {

        const sizeRef = useRef();
        const [boxSize, setBoxSize] = useState({ width: 0, heigth: 0 })
        return () => {
            const { width, heigth } = sizeRef.current.getboundingClientRect();
            setBoxSize({ width, heigth })
        };
    }, [quote])

    return (
        <>
            <blockquote className='bloquote text-end' style={{ display: 'flex' }} >
                <h3 className='mb-1' > {quote} </h3>
                <br />
                <footer className='blockquote-footer' > {author} </footer>
            </blockquote>
            <code>{ JSON.stringify(boxSize) }</code>
        </>
    )
}
