import { useState, useEffect } from 'react'
import { Message } from './Message';

export const FormWithCustomHook = () => {

    const [ formState, setFormState ] = useState( {
        user: '',
        email: '',
        pass: '',
    } ) ;

    const { user, email, pass } = formState

    const onInputChange = ( { target } ) => {
        const { name, value } = target
        setFormState( {
            ...formState,
            [ name ]: value
        } )
    }

    // useEffect(() => {
    //     // console.log('Use Efect')
    // }, [] ) ;
    // useEffect(() => {
    //     // console.log('Use Efect')
    // }, [ formState ] ) ;
    // useEffect(() => {
    //     // console.log('Use Efect')
    // }, [ email ] ) ;


  return (
    <>
        <h1>FormWithCustomHook</h1>
        <hr />
        <input type="text" className='form-control' placeholder='User' name='user' value={ user } onChange={ onInputChange } />
        <input type="text" className='form-control mt-2' placeholder='Email' name='email' value={ email } onChange={ onInputChange } />
        <input type="text" className='password form-control mt-2' placeholder='Password' name='pass' value={ pass } onChange={ onInputChange } />
        { user === 'Vegeta' && <Message /> }
    </>
  )
}
