import { useState } from "react" ;

export const useCounter = ( initialValue = 10 ) => {

    const [counter, setCounter] = useState( initialValue ) ;

    const increment = () => {
        setCounter( counter + 1 ) ;
    }
    const decrement = () => {
        setCounter( counter - 1 ) ;
    }
    const reset = () => {
        setCounter( counter = initialValue ) ;
    }

    return {
        counter,
        increment,
    }
}
