import { PropTypes } from "prop-types";


export const TodoList = ( { props } ) => {
    return (
        <ul className="list-group">
            {
                props.map(prop => (
                    <li key={prop.id} className="list-group-item d-flex justify-content-between">
                        <span className="align-self-center">prop.description </span>
                        <button className="btn btn-danger">Quitar</button>
                    </li>

                ))
            }
        </ul>
    )
}

TodoList.propType = {
    id: PropTypes.number,
    description: PropTypes.string,
}
