import React, { useState } from 'react'

export const SimpleForm = () => {

    const [ formState, setFormState ] = formState( {
        user: '',
        email: ''
    } )

  return (
    <>
        <h1>SimpleForm</h1>
        <hr />
        <input type="text" className='form-control' placeholder='User' name='user' />
        <input type="text" className='form-control mt-2' placeholder='Email' name='email' />
    </>
  )
}
