import { useEffect, useReducer } from "react"
import { todoReducer } from "./todoReducer"
import { TodoList } from "./TodoList"
import { AddTodo } from "./AddTodo"

const initialState = [
    {
        id: new Date().getTime(), 
        desc: 'Conseguir la esfera de una estrella',
        done: true
    },
    {
        id: new Date().getTime() * 3, 
        desc: 'Conseguir la esfera de dos estrellas',
        done: true
    },
]

const init = () => {
    return JSON.parse( localStorage.getItem( 'todos' ) ) || [] ;
}

export const TodoApp = () => {

    // Con esto aparecen como hooks en el componente como si fuese un state 

    const [toDos, dispatch, init] = useReducer( todoReducer, initialState )

    useEffect(() => {
      localStorage.setItem('todos', JSON.stringify(toDos))
    }, [toDos])


    const handleNewTod = (todo) => {
        const action = {
            type: 'addTodo',
            payload: todo
        }
        dispatch( action )
    }

    return (
        <>
            <h1>Todo App ðŸ¤¯</h1>
            <div className="row">
                <div className="col-7">
                    <TodoList list={ toDos } />
                </div>
                <div className="col-5">
                    <h4>Agregar ToDo</h4>
                    <hr />
                    <AddTodo onNewTodo={ handleNewTod } />
                </div>
            </div>
        </>
    )
}
