import { useState } from "react";

export const useForm = ( initialForm = {} ) => {

    const [ formState, setFormState ] = useState( initialForm ) ;

    const onInputChange = ( { target } ) => {
        const { name, value } = target
        setFormState( {
            ...formState,
            [ name ]: value
        } )
    }

    const onResetForm = (  ) => {
        setFormState( initialForm )
    }

    const onNewTodo = ( ) => {
        console.log('submit')
    }

    return {
        ...formState,
        formState,
        onInputChange,
        onResetForm,
        onNewTodo
    }
}
